import React, { useState, useEffect, useRef, useCallback } from 'react'; // Added useCallback
import { Stage, Layer, Line, Rect, Circle, Text, Transformer } from 'react-konva';
import { useParams, useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';
import Video from '@twilio/video-sdk';  // Import Twilio Video SDK
import './WhiteboardRoom.css'; // Make sure this CSS exists

const socket = io('http://192.168.1.102:5000'); // Replace with your PC's IP

// Helper component for selectable/transformable Konva nodes (unchanged)
const ColoredRect = ({ shapeProps, isSelected, onSelect, onChange, activeTool }) => {
  const shapeRef = useRef();
  const trRef = useRef();
  useEffect(() => {
    if (isSelected && trRef.current && shapeRef.current) {
      trRef.current.nodes([shapeRef.current]);
      trRef.current.getLayer().batchDraw();
    } else if (!isSelected && trRef.current) {
      trRef.current.nodes([]);
      trRef.current.getLayer().batchDraw();
    }
  }, [isSelected, shapeProps.type]);
  const handleDblClick = (e) => {
    onSelect();
    if (shapeProps.type === 'text') {
      setTimeout(() => {
        if (trRef.current) {
          trRef.current.nodes([]);
          trRef.current.getLayer().batchDraw();
        }
        const textNode = shapeRef.current;
        if (!textNode) return;
        textNode.hide();
        textNode.getLayer().batchDraw();
        const textPosition = textNode.absolutePosition();
        const stageBox = textNode.getStage().container().getBoundingClientRect();
        const textarea = document.createElement('textarea');
        document.body.appendChild(textarea);
        textarea.value = textNode.text();
        textarea.style.position = 'absolute';
        textarea.style.top = (textPosition.y + stageBox.top) + 'px';
        textarea.style.left = (textPosition.x + stageBox.left) + 'px';
        textarea.style.width = textNode.width() - textNode.padding() * 2 + 'px';
        textarea.style.height = textNode.height() - textNode.padding() * 2 + 5 + 'px';
        textarea.style.fontSize = textNode.fontSize() + 'px';
        textarea.style.border = 'none';
        textarea.style.padding = '0px';
        textarea.style.margin = '0px';
        textarea.style.overflow = 'hidden';
        textarea.style.background = 'none';
        textarea.style.outline = 'none';
        textarea.style.resize = 'none';
        textarea.style.lineHeight = textNode.lineHeight();
        textarea.style.fontFamily = textNode.fontFamily();
        textarea.style.transformOrigin = 'left top';
        textarea.style.textAlign = textNode.align();
        textarea.style.color = textNode.fill();
        textarea.style.transform = `rotateZ(${textNode.rotation()}deg)`;
        textarea.style.zIndex = '9999';
        textarea.focus();
        const removeTextarea = () => {
          document.body.removeChild(textarea);
          textNode.show();
          textNode.getLayer().batchDraw();
        };
        textarea.addEventListener('keydown', (e) => {
          if (e.key === 'Enter' && !e.shiftKey) {
            textarea.blur();
          }
          if (e.key === 'Escape') {
            removeTextarea();
          }
        });
        textarea.addEventListener('blur', () => {
          onChange({
            ...shapeProps,
            text: textarea.value,
          });
          removeTextarea();
        });
      }, 0);
    }
  };
  const currentKonvaNode = (
    shapeProps.type === 'rect' ? (
      <Rect
        onClick={onSelect} onTap={onSelect} onDblClick={handleDblClick} onDblTap={handleDblClick}
        ref={shapeRef} {...shapeProps} draggable={activeTool === 'select'}
        onDragEnd={(e) => { onChange({ ...shapeProps, x: e.target.x(), y: e.target.y(), }); }}
        onTransformEnd={(e) => {
          const node = shapeRef.current; const scaleX = node.scaleX(); const scaleY = node.scaleY();
          node.scaleX(1); node.scaleY(1);
          onChange({ ...shapeProps, x: node.x(), y: node.y(), width: Math.max(5, node.width() * scaleX), height: Math.max(5, node.height() * scaleY), rotation: node.rotation(), });
        }} />
    ) : shapeProps.type === 'circle' ? (
      <Circle
        onClick={onSelect} onTap={onSelect} onDblClick={handleDblClick} onDblTap={handleDblClick}
        ref={shapeRef} {...shapeProps} draggable={activeTool === 'select'}
        onDragEnd={(e) => { onChange({ ...shapeProps, x: e.target.x(), y: e.target.y(), }); }}
        onTransformEnd={(e) => {
          const node = shapeRef.current; const scaleX = node.scaleX(); node.scaleX(1); node.scaleY(1);
          onChange({ ...shapeProps, x: node.x(), y: node.y(), radius: Math.max(5, node.radius() * scaleX), rotation: node.rotation(), });
        }} />
    ) : shapeProps.type === 'text' ? (
      <Text
        onClick={onSelect} onTap={onSelect} onDblClick={handleDblClick}   onDblTap={handleDblClick} 
        ref={shapeRef} {...shapeProps} draggable={activeTool === 'select'}
        onDragEnd={(e) => { onChange({ ...shapeProps, x: e.target.x(), y: e.target.y(), }); }}
        onTransformEnd={(e) => {
          const node = shapeRef.current; const scaleX = node.scaleX(); const scaleY = node.scaleY();
          node.scaleX(1); node.scaleY(1);
          onChange({ ...shapeProps, x: node.x(), y: node.y(), width: Math.max(5, node.width() * scaleX), height: Math.max(5, node.height() * scaleY), fontSize: Math.max(8, node.fontSize() * scaleX), rotation: node.rotation(), });
        }} />
    ) : null
  );
  return (
    <>
      {currentKonvaNode}
      {isSelected && currentKonvaNode && (
        <Transformer ref={trRef} boundBoxFunc={(oldBox, newBox) => { if (newBox.width < 5 || newBox.height < 5) { return oldBox; } return newBox; }} />
      )}
    </>
  );
};

function WhiteboardRoom() {
  const { roomName } = useParams();
  const navigate = useNavigate();
  const [drawingObjects, setDrawingObjects] = useState([]);
  const [isDrawing, setIsDrawing] = useState(false);
  const stageRef = useRef(null);
  const currentObjectId = useRef(null);

  const [chatMessages, setChatMessages] = useState([]);
  const [newChatMessage, setNewChatMessage] = useState('');
  const chatMessagesEndRef = useRef(null);

  const [stageDimensions, setStageDimensions] = useState({
    width: window.innerWidth * 0.7,
    height: window.innerHeight * 0.8
  });
  const canvasContainerRef = useRef(null);

  const [tool, setTool] = useState('pen');
  const [strokeColor, setStrokeColor] = useState('#000000');
  const [strokeWidth, setStrokeWidth] = useState(5);
  const [fillColor, setFillColor] = useState('transparent');
  const [selectedId, selectShape] = useState(null);

  // --- NEW: Video Call States ---
  const [videoRoom, setVideoRoom] = useState(null); // Twilio Video Room object
  const [localParticipant, setLocalParticipant] = useState(null); // Local participant object
  const [remoteParticipants, setRemoteParticipants] = useState([]); // Array of remote participants
  const [localVideoTrack, setLocalVideoTrack] = useState(null); // Local video track
  const [localAudioTrack, setLocalAudioTrack] = useState(null); // Local audio track
  const localVideoRef = useRef(null); // Ref for local video element
  const remoteVideosRef = useRef(null); // Ref for container of remote videos

  const [videoCallEnabled, setVideoCallEnabled] = useState(false); // Toggle video call UI
  const [isVideoConnected, setIsVideoConnected] = useState(false); // Status of video connection

  // Function to attach a track to a DOM element
  const attachTrack = useCallback((track, container) => {
    if (track && container) {
      container.appendChild(track.attach());
    }
  }, []);

  // Function to detach a track from a DOM element
  const detachTrack = useCallback((track) => {
    if (track) {
      track.detach().forEach(element => element.remove());
    }
  }, []);

  // Handle participant connections/disconnections
  const participantConnected = useCallback((participant) => {
    console.log(`Participant "${participant.identity}" connected`);
    setRemoteParticipants(prevParticipants => [...prevParticipants, participant]);

    participant.on('trackSubscribed', track => {
      console.log(`Track "${track.kind}" subscribed for participant "${participant.identity}"`);
      if (track.kind === 'video') {
        const participantDiv = document.getElementById(`participant-${participant.sid}`);
        if (participantDiv) {
          attachTrack(track, participantDiv);
        } else {
          // If div not yet rendered, create one and attach
          const newDiv = document.createElement('div');
          newDiv.id = `participant-${participant.sid}`;
          newDiv.className = 'remote-video-container';
          remoteVideosRef.current.appendChild(newDiv);
          attachTrack(track, newDiv);
        }
      } else if (track.kind === 'audio') {
        attachTrack(track, document.body); // Audio can be attached to body
      }
    });

    participant.on('trackUnsubscribed', track => {
      console.log(`Track "${track.kind}" unsubscribed from participant "${participant.identity}"`);
      detachTrack(track);
    });
  }, [attachTrack, detachTrack]);

  const participantDisconnected = useCallback((participant) => {
    console.log(`Participant "${participant.identity}" disconnected`);
    setRemoteParticipants(prevParticipants => prevParticipants.filter(p => p.sid !== participant.sid));
    // Clean up detached tracks
    participant.tracks.forEach(publication => {
      if (publication.isSubscribed) {
        detachTrack(publication.track);
      }
    });
    // Remove participant's video container if exists
    const participantDiv = document.getElementById(`participant-${participant.sid}`);
    if (participantDiv) {
      participantDiv.remove();
    }
  }, [detachTrack]);

  // --- Twilio Video Connection Logic ---
  const connectToVideoRoom = useCallback(async (identity) => {
    if (!identity || !roomName) {
      console.error("Identity or room name missing for video connection.");
      alert("Cannot join video: Identity or room name missing.");
      return;
    }

    try {
      // 1. Get Twilio Token from backend
      const { token, error } = await new Promise(resolve => {
        socket.emit('getTwilioToken', { roomName, identity }, resolve);
      });

      if (error || !token) {
        console.error("Failed to get Twilio token:", error);
        alert("Failed to get video token. Check backend console and Twilio credentials.");
        return;
      }

      // 2. Connect to Twilio Video Room
      console.log(`Connecting to Twilio video room "${roomName}" with token...`);
      const room = await Video.connect(token, {
        name: roomName,
        audio: true, // Request audio track
        video: { width: 640, height: 480 }, // Request video track
        dominantSpeaker: true // Enable dominant speaker detection
      });

      setVideoRoom(room);
      setLocalParticipant(room.localParticipant);
      setIsVideoConnected(true);

      console.log(`Successfully connected to Room: ${room.name}`);

      // Attach local participant's tracks
      room.localParticipant.tracks.forEach(publication => {
        if (publication.track) {
          if (publication.kind === 'video') {
            attachTrack(publication.track, localVideoRef.current);
            setLocalVideoTrack(publication.track);
          } else if (publication.kind === 'audio') {
            setLocalAudioTrack(publication.track);
          }
        }
      });

      // Attach tracks of existing remote participants
      room.participants.forEach(participantConnected);

      // Handle new participants joining
      room.on('participantConnected', participantConnected);
      room.on('participantDisconnected', participantDisconnected);

      // Handle room disconnection
      room.on('disconnected', (room, error) => {
        console.log('Disconnected from Twilio Room:', error);
        // Clean up all tracks
        room.localParticipant.tracks.forEach(publication => detachTrack(publication.track));
        room.participants.forEach(participant => {
          participant.tracks.forEach(publication => detachTrack(publication.track));
        });
        setVideoRoom(null);
        setLocalParticipant(null);
        setRemoteParticipants([]);
        setLocalVideoTrack(null);
        setLocalAudioTrack(null);
        setIsVideoConnected(false);
        alert("You have been disconnected from the video call.");
      });

    } catch (error) {
      console.error('Failed to connect to Twilio Video Room:', error);
      alert('Failed to connect to video call. Please ensure camera/mic permissions.');
      setIsVideoConnected(false);
    }
  }, [roomName, attachTrack, detachTrack, participantConnected, participantDisconnected]);

  // Function to disconnect from video room
  const disconnectFromVideoRoom = useCallback(() => {
    if (videoRoom) {
      videoRoom.disconnect();
      console.log('Disconnected from Twilio video room.');
    }
  }, [videoRoom]);

  // Main useEffect for Socket.IO and Room state management
  useEffect(() => {
    if (!roomName) {
      navigate('/');
      return;
    }

    socket.emit('joinRoom', roomName);

    socket.on('connect', () => {
      console.log('Connected to backend Socket.IO:', socket.id);
    });

    socket.on('disconnect', () => {
      console.log('Disconnected from backend Socket.IO');
      alert('Disconnected from server. Please refresh.');
      navigate('/');
    });

    socket.on('whiteboardState', (data) => {
      console.log('Received initial room state:', data);
      setDrawingObjects(data.lines);
      setChatMessages(data.messages);
    });

    socket.on('drawingStarted', (newObject) => {
        setDrawingObjects((prevObjects) => [...prevObjects, newObject]);
    });

    socket.on('drawing', (data) => {
        const { objectId, newPoints } = data;
        setDrawingObjects((prevObjects) => {
            return prevObjects.map((obj) => {
                if (obj.id === objectId && obj.type === 'line') {
                    return { ...obj, points: obj.points.concat(newPoints) };
                }
                return obj;
            });
        });
    });

    socket.on('drawingFinished', (data) => {
        const { objectId, finalObjectState } = data;
        setDrawingObjects((prevObjects) => {
            return prevObjects.map((obj) => {
                if (obj.id === objectId) {
                    return finalObjectState;
                }
                return obj;
            });
        });
    });

    socket.on('objectUpdated', (data) => {
        const { objectId, newAttributes } = data;
        setDrawingObjects((prevObjects) => {
            return prevObjects.map((obj) => {
                if (obj.id === objectId) {
                    return { ...obj, ...newAttributes };
                }
                return obj;
            });
        });
    });

    socket.on('whiteboardCleared', () => {
        setDrawingObjects([]);
        selectShape(null);
        console.log('Whiteboard cleared by another user.');
    });

    socket.on('chatMessage', (message) => {
        setChatMessages((prevMessages) => [...prevMessages, message]);
    });

    socket.on('userJoined', (username) => {
        setChatMessages((prevMessages) => [...prevMessages, {
            username: 'System',
            message: `${username} has joined the room.`,
            timestamp: new Date()
        }]);
    });

    socket.on('roomDestroyed', (destroyedRoomName) => {
        if (destroyedRoomName === roomName) {
            alert(`Room "${destroyedRoomName}" has been closed or automatically destroyed.`);
            navigate('/');
        }
    });

    const handleResize = () => {
      if (canvasContainerRef.current) {
        const { clientWidth, clientHeight } = canvasContainerRef.current;
        setStageDimensions({ width: clientWidth, height: clientHeight });
      }
    };

    window.addEventListener('resize', handleResize);
    handleResize();

    return () => {
      window.removeEventListener('resize', handleResize);
      socket.off('connect');
      socket.off('disconnect');
      socket.off('whiteboardState');
      socket.off('drawingStarted');
      socket.off('drawing');
      socket.off('drawingFinished');
      socket.off('objectUpdated');
      socket.off('whiteboardCleared');
      socket.off('chatMessage');
      socket.off('userJoined');
      socket.off('roomDestroyed');

      // NEW: Clean up Twilio connection on component unmount/room change
      disconnectFromVideoRoom();
    };
  }, [roomName, navigate, disconnectFromVideoRoom]);

  // Effect to scroll chat to bottom
  useEffect(() => {
    chatMessagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [chatMessages]);

  const handleObjectChange = useCallback((updatedObject) => {
    setDrawingObjects((prevObjects) => {
      return prevObjects.map((obj) => {
        if (obj.id === updatedObject.id) {
          return updatedObject;
        }
        return obj;
      });
    });
    socket.emit('updateObject', { roomName, objectId: updatedObject.id, newAttributes: updatedObject });
  }, [roomName]); // Add roomName to dependency array

  const handleMouseDown = (e) => {
    const clickedOnEmpty = e.target === e.target.getStage();
    if (tool === 'select' && clickedOnEmpty) {
      selectShape(null);
      return;
    }
    if (e.evt.type === 'touchstart') { e.evt.preventDefault(); }
    setIsDrawing(true);
    const pos = e.target.getStage().getPointerPosition();
    const newObjectId = `${socket.id}-${Date.now()}`;
    currentObjectId.current = newObjectId;
    selectShape(null);

    let newObject;
    switch (tool) {
      case 'pen':
      case 'eraser': newObject = { id: newObjectId, type: 'line', points: [pos.x, pos.y], stroke: tool === 'eraser' ? '#FFFFFF' : strokeColor, strokeWidth: tool === 'eraser' ? strokeWidth + 10 : strokeWidth, tool: tool }; break;
      case 'rect': newObject = { id: newObjectId, type: 'rect', x: pos.x, y: pos.y, width: 0, height: 0, stroke: strokeColor, strokeWidth: strokeWidth, fill: fillColor, }; break;
      case 'circle': newObject = { id: newObjectId, type: 'circle', x: pos.x, y: pos.y, radius: 0, stroke: strokeColor, strokeWidth: strokeWidth, fill: fillColor, }; break;
      case 'text':
        newObject = { id: newObjectId, type: 'text', x: pos.x, y: pos.y, text: 'Double click to edit', fontSize: 20, fontFamily: 'Arial', stroke: strokeColor, fill: strokeColor, width: 200, height: 30, rotation: 0 };
        setIsDrawing(false); socket.emit('startDrawing', { roomName, object: newObject }); setDrawingObjects((prevObjects) => [...prevObjects, newObject]); selectShape(newObjectId); return;
      case 'select': setIsDrawing(false); return;
      default: return;
    }
    setDrawingObjects((prevObjects) => [...prevObjects, newObject]);
    socket.emit('startDrawing', { roomName, object: newObject });
  };

  const handleMouseMove = (e) => {
    if (!isDrawing) { return; }
    if (e.evt.type === 'touchmove') { e.evt.preventDefault(); }
    const stage = e.target.getStage(); const point = stage.getPointerPosition();
    let currentObject = drawingObjects.find(obj => obj.id === currentObjectId.current);

    if (currentObject) {
      const newObjects = [...drawingObjects]; const index = newObjects.findIndex(obj => obj.id === currentObject.id);
      switch (currentObject.type) {
        case 'line': currentObject.points = currentObject.points.concat([point.x, point.y]); newObjects[index] = currentObject; setDrawingObjects(newObjects); socket.emit('drawing', { roomName, objectId: currentObjectId.current, newPoints: [point.x, point.y] }); break;
        case 'rect':
          const newX_rect = Math.min(currentObject.x, point.x); const newY_rect = Math.min(currentObject.y, point.y);
          const newWidth_rect = Math.abs(point.x - currentObject.x); const newHeight_rect = Math.abs(point.y - currentObject.y);
          currentObject = { ...currentObject, x: newX_rect, y: newY_rect, width: newWidth_rect, height: newHeight_rect }; newObjects[index] = currentObject; setDrawingObjects(newObjects); socket.emit('updateObject', { roomName, objectId: currentObjectId.current, newAttributes: { x: newX_rect, y: newY_rect, width: newWidth_rect, height: newHeight_rect } }); break;
        case 'circle':
          const newRadius_circle = Math.sqrt(Math.pow(point.x - currentObject.x, 2) + Math.pow(point.y - currentObject.y, 2));
          currentObject = { ...currentObject, radius: newRadius_circle }; newObjects[index] = currentObject; setDrawingObjects(newObjects); socket.emit('updateObject', { roomName, objectId: currentObjectId.current, newAttributes: { radius: newRadius_circle } }); break;
        default: break;
      }
    }
  };

  const handleMouseUp = () => {
    setIsDrawing(false);
    if (tool !== 'select' && tool !== 'text') {
      const finalObject = drawingObjects.find(obj => obj.id === currentObjectId.current);
      if (finalObject) { socket.emit('endDrawing', { roomName, objectId: currentObjectId.current, finalObjectState: finalObject }); }
    }
    currentObjectId.current = null;
  };

  const handleSendChatMessage = () => {
    if (newChatMessage.trim()) { socket.emit('chatMessage', { roomName, message: newChatMessage.trim() }); setNewChatMessage(''); }
  };
  const handleChatKeyPress = (e) => { if (e.key === 'Enter') { handleSendChatMessage(); } };
  const handleClearWhiteboard = () => { if (window.confirm('Are you sure you want to clear the entire whiteboard? This cannot be undone!')) { socket.emit('clearWhiteboard', roomName); } };
  const handleCloseRoom = () => { if (window.confirm('Are you sure you want to close this room? All users will be disconnected and data will be deleted.')) { socket.emit('closeRoom', roomName); navigate('/'); alert('Room closure initiated.'); } };

  const getCursorStyle = () => {
    switch (tool) {
      case 'pen': case 'eraser': case 'rect': case 'circle': return 'crosshair';
      case 'text': return 'text'; case 'select': return selectedId ? 'move' : 'default';
      default: return 'default';
    }
  };

  // --- NEW: Video Call UI Toggle and Join/Leave handlers ---
  const toggleVideoCallUI = () => {
    setVideoCallEnabled(prev => !prev);
    // If enabling video UI and not already connected, try to connect
    if (!videoCallEnabled && !isVideoConnected) {
        connectToVideoRoom(socket.id); // Using socket.id as identity for now
    } else if (videoCallEnabled && isVideoConnected) {
        disconnectFromVideoRoom(); // If disabling video UI and connected, disconnect
    }
  };

  const handleJoinLeaveVideoCall = () => {
    if (isVideoConnected) {
      disconnectFromVideoRoom();
    } else {
      connectToVideoRoom(socket.id); // Use socket.id as identity
    }
  };


  return (
    <div className="whiteboard-page-container">
      <div className="whiteboard-main-content">
        <div className="header">
          <h1>Whiteboard Room: <span className="room-name">{roomName}</span></h1>
          <button onClick={() => navigate('/')} className="leave-button">
            Leave Room
          </button>
        </div>

        {/* Professional Tool Palette */}
        <div className="tool-palette">
          <button className={`tool-button ${tool === 'select' ? 'active' : ''}`} onClick={() => { setTool('select'); selectShape(null); }} title="Select & Move">ðŸ‘† Select</button>
          <button className={`tool-button ${tool === 'pen' ? 'active' : ''}`} onClick={() => setTool('pen')} title="Pen">âœï¸ Pen</button>
          <button className={`tool-button ${tool === 'eraser' ? 'active' : ''}`} onClick={() => setTool('eraser')} title="Eraser">ðŸ§½ Eraser</button>
          <div className="tool-separator"></div>
          <button className={`tool-button ${tool === 'rect' ? 'active' : ''}`} onClick={() => setTool('rect')} title="Rectangle">â—»ï¸ Rectangle</button>
          <button className={`tool-button ${tool === 'circle' ? 'active' : ''}`} onClick={() => setTool('circle')} title="Circle">âš« Circle</button>
          <button className={`tool-button ${tool === 'text' ? 'active' : ''}`} onClick={() => setTool('text')} title="Text">ðŸ…°ï¸ Text</button>

          <div className="tool-separator"></div>

          <input type="color" className="color-picker" value={strokeColor} onChange={(e) => { setStrokeColor(e.target.value); if (tool !== 'eraser') setTool('pen'); }} title="Stroke Color" />
          <input type="color" className="color-picker" value={fillColor} onChange={(e) => { setFillColor(e.target.value); if (tool === 'pen' || tool === 'eraser') { setTool('rect'); } }} title="Fill Color" />

          <input type="range" min="1" max="20" value={strokeWidth} onChange={(e) => setStrokeWidth(parseInt(e.target.value, 10))} className="stroke-width-slider" title={`Stroke Width: ${strokeWidth}px`} />
          <span className="stroke-width-display">{strokeWidth}px</span>

          <div className="tool-separator"></div>

          <button className="tool-button" onClick={handleClearWhiteboard} title="Clear All">ðŸ—‘ï¸ Clear All</button>
          <button className="tool-button danger-button" onClick={handleCloseRoom} title="Close & Delete Room">âŒ Close Room</button>

          {/* NEW: Video Call Button */}
          <button
            className={`tool-button ${videoCallEnabled ? 'active success-button' : ''}`}
            onClick={toggleVideoCallUI}
            title={videoCallEnabled ? 'Hide Video Call' : 'Show Video Call'}
          >
            {videoCallEnabled ? 'ðŸ“¹ Hide Video' : 'ðŸ“¹ Show Video'}
          </button>
        </div>

        <div className="whiteboard-canvas-container" ref={canvasContainerRef}>
          <Stage
            width={stageDimensions.width} height={stageDimensions.height}
            onMouseDown={handleMouseDown} onMousemove={handleMouseMove} onMouseup={handleMouseUp}
            onTouchStart={handleMouseDown} onTouchMove={handleMouseMove} onTouchEnd={handleMouseUp}
            ref={stageRef}
            style={{ cursor: getCursorStyle(), backgroundColor: 'white', touchAction: 'none' }}
          >
            <Layer>
              {drawingObjects.map((obj) => {
                if (obj.type === 'line') {
                  return (<Line key={obj.id} points={obj.points} stroke={obj.stroke} strokeWidth={obj.strokeWidth} tension={0.5} lineCap="round" lineJoin="round" globalCompositeOperation={obj.tool === 'eraser' ? 'destination-out' : 'source-over'} />);
                } else {
                  return (
                    <ColoredRect
                      key={obj.id} shapeProps={obj} isSelected={obj.id === selectedId}
                      onSelect={() => { selectShape(obj.id); setTool('select'); }}
                      onChange={handleObjectChange} activeTool={tool} />
                  );
                }
              })}
            </Layer>
          </Stage>
        </div>
        <p className="hint-text margin-top-md">Use the tools above to draw and collaborate!</p>
      </div>

      {/* Main Chat Sidebar - remains mostly unchanged */}
      <div className="chat-sidebar">
        <h2>Room Chat</h2>
        {/* NEW: Video Call Section (conditionally rendered) */}
        {videoCallEnabled && (
          <div className="video-call-section">
            <div className="video-controls">
              <button
                className={`tool-button ${isVideoConnected ? 'danger-button' : 'success-button'}`}
                onClick={handleJoinLeaveVideoCall}
              >
                {isVideoConnected ? 'ðŸ“ž Disconnect' : 'ðŸ“ž Connect Video'}
              </button>
              {/* Add mute/unmute buttons here later */}
            </div>
            <div className="local-video-container" ref={localVideoRef}>
                {/* Local participant's video will be attached here */}
                {!localVideoTrack && <p>Waiting for local video...</p>}
            </div>
            <div className="remote-videos-grid" ref={remoteVideosRef}>
                {/* Remote participants' videos will be attached here */}
                {remoteParticipants.length === 0 && isVideoConnected && <p>Waiting for others...</p>}
            </div>
          </div>
        )}
        <div className="chat-messages">
          {chatMessages.map((msg, index) => (
            <div key={index} className="chat-message">
              <span className="chat-username">{msg.username}:</span> {msg.message}
              <span className="chat-timestamp">{new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
            </div>
          ))}
          <div ref={chatMessagesEndRef} />
        </div>
        <div className="chat-input-group">
          <input type="text" placeholder="Type your message..." value={newChatMessage} onChange={(e) => setNewChatMessage(e.target.value)} onKeyPress={handleChatKeyPress} className="chat-input" />
          <button onClick={handleSendChatMessage} className="send-button">Send</button>
        </div>
      </div>
    </div>
  );
}

export default WhiteboardRoom;
